sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Using scripts for glide form and glide user
	var whatneeded = g_form.getValue('u_what_needed');
   //clearing all the choices from the whatneeded variable if there are
	g_form.clearOptions('u_what_needed');

	//now checking these fields one by one by their departments
	if(newValue=='hr'){
		g_form.addOption('u_what_needed','hr1','Human Resource 1');
		g_form.addOption('u_what_needed','hr2','Human Resource 2');
		g_form.addOption('u_what_needed','other','other');
	}
	//now checking for the facilities
	if(newValue=='facilities'){
		g_form.addOption('u_what_needed','facilities1','facilities 1');
		g_form.addOption('u_what_needed','facilities2','facilities 2');
		g_form.addOption('u_what_needed','other','other');
	}	
	if(newValue=='legal'){
		g_form.addOption('u_what_needed','legal1','legal 1');
		g_form.addOption('u_what_needed','legal2','legal ');
		g_form.addOption('u_what_needed','other','other');
	}
	//Now checking if the form is not new and then we are gonna se the last value from that record and also it is loading some existing program 
	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed', whatneeded);
	}
}
